import React, { useState, useEffect } from "react";

const ShowHide = () => {
   const [size, setSize] = useState(window.innerWidth);

  const checkSize = () => {
    setSize(window.innerWidth)
  }

  useEffect(() => {
    window.addEventListener('resize', checkSize)
  }, []);

  return (
    <section>
      <h1>Window</h1>
      <h2>{size}px</h2>
    </section>
  );
};


export default ShowHide;
