import React from "react";
import { data } from "../../../data";

const UseStateArray = () => {
  const [people, setPeople] = React.useState(data);
  console.log(people);
  return (
    <>
      {people.map(({item, name}, key) => {
        return <h1 className={key}>{item.id}</h1>
      })}
    </>
  )
};

export default UseStateArray;
