import React, { useState, useEffect } from "react";

// cleanup function
// second argument

const UseEffectCleanup = () => {
  const [size, setSize] = useState(window.innerWidth);

  const checkSize = () => {
    setSize(window.innerWidth)
  }

  useEffect(() => {
    setSize(window.innerWidth);
  }, [size]);

  return (
    <section>
      <h1>Window</h1>
      <h2>{size}px</h2>;
    </section>
  );
};

export default UseEffectCleanup;
