import React, { useState, useEffect } from 'react';

// cleanup function
// second argument

const UseEffectCleanup = () => {
  const[size, setSize] = useState(window.innerWidth)

  useEffect(() => {
    setSize(window.innerWidth)
  },[size])

  return 
  <h2>{size}px</h2>;
};

export default UseEffectCleanup;
