import React, { useState, useEffect } from "react";
// by default runs after every re-render
// cleanup function
// second parameter
const UseEffectBasics = () => {
  const [count, setCount] = useState(0);

  useEffect(() => {
    document.title = `Você clicou ${4} vezes`;
  }, []);

  return ;
};

export default UseEffectBasics;
