import React, { useState } from "react";

const UseStateCounter = () => {
  const [count, setCount] = useState(0);

  return (
    <>
      <h1>Counter</h1>
      <h2>{count}</h2>
      <button className="btn" onClick={() => setCount(count + 1)}>
        increase
      </button>
      <button className="btn" onClick={() => setCount(count - 1)}>
        decrease
      </button>
      <button className="btn" onClick={() => setCount(0)}>
        restart
      </button>
    </>

  );
};

export default UseStateCounter;
