import React, { useState } from "react";

const personInfo = {
  name: "alessandro",
  age: 26,
  message: "random message",
};

const UseStateObject = () => {
  const [person, setPerson] = useState(personInfo);
  return (
    <div>
      {Object.values(person).map((info, index) => {
        return <h1>{info}</h1>;
      })}
    </div>
  );
};

export default UseStateObject;
