import React, { useState, useEffect } from "react";

const url = "https://api.github.com/users";

const UseEffectFetchData = () => {
  const[getData, setGetData] = useState()

  useEffect(
    fetch(url)
      .then((resp) => resp.json())
      .then((data) => setGetData(data) )
  );
  return <h2></h2>;
};

export default UseEffectFetchData;
