import React, { useState, useEffect } from "react";

const ShowHide = () => {
  const[size, setSize] = useState()

  const checkSize = () => {
    setSize(window.innerWidth)
  }

  useEffect(() => {
    window.addEventListener('resize', checkSize() )
  },[size])

  return (
    <section>
      <h1>Window Size</h1>
      <h2>{size}PX</h2>
    </section>
  );
};

export default ShowHide;
